mapper.update:
UPDATE sys_menu_info
SET menu_status = #{type}
where id = #{id}

UPDATE sys_menu_info
SET is_del = '1'
where id = #{id}

mapper.select:
SELECT m.id,m.menu_name,m.menu_status,m.is_del,m.menu_action,m.is_child_node,m.create_time,m.update_time,m.menu_order,m.menu_remarks,m.creator,m.updator,u.real_name AS creatorName,ui.real_name AS updatorName FROM sys_menu_info m LEFT JOIN sys_user_info u ON m.creator=u.id LEFT JOIN sys_user_info ui ON m.updator=ui.id WHERE m.is_del=0 and m.pid = #{pid}
and m.menu_name LIKE '%${menuName}%'
and m.menu_status = #{menuStatus}
ORDER BY m.menu_order ASC
