mapper.update:
UPDATE sys_role_info
SET is_del = 1
WHERE id = #{id}

UPDATE sys_role_info
SET role_name   = #{roleName},
    remarks     = #{remarks},
    update_time = #{updateTime},
    updator     = #{updator}
WHERE id = #{id}

UPDATE sys_user_role
SET is_del = 1
WHERE role_id = #{id}

UPDATE sys_role_menu
SET is_del = 1
where role_id = #{roleId}
