mapper.update:
UPDATE sys_dic
SET is_del = 1
WHERE id = #{id}

mapper.select:
SELECT s.id, s.dic_code, s.dic_name, s.dic_order, s.dic_type, s.create_time, s.creator, s.update_time, s.updator, s.is_del, s.pid, s.remarks, u.real_name AS creatorName, ui.real_name AS updatorName FROM sys_dic s LEFT JOIN user_info u ON s.creator = u.id LEFT JOIN user_info ui ON s.updator = ui.id WHERE s.is_del = 0 and s.pid = #{pid}
and s.dic_name LIKE '%${dicName}%'
ORDER BY s.dic_order ASC
